import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Typography, Button, ButtonGroup, Container, FormControlLabel, FormLabel, FormControl } from '@material-ui/core';
import { Radio, RadioGroup } from '@material-ui/core';
import { KeyboardArrowRight } from '@material-ui/icons';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles({
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  }
})



export default function Create() {
  const classes = useStyles();
  const [title, setTitle] = useState('');
  const [details, setDetails] = useState('');
  const [titleError, setTitleError] = useState(false);
  const [detailsError, setDetailsError] = useState(false);
  const [category, setCategory] = useState('todos');
  const history = useHistory();

  const onSubmit = e => {
    e.preventDefault();
    setTitleError(false);
    setDetailsError(false);

    if (titleError == '') {
      setTitleError(true)
    }

    if (detailsError == '') {
      setDetailsError(true)
    }

    if (title && details) {
      fetch('http://localhost:8000/note', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, details, category })
      })
        .then(() => {
          history.push('/')
        })
    }
  }

  return (
    <Container>
      <Typography
        className={classes.title}
        variant='h6'
        component='h2'
        color='textSecondary'
        gutterBottom
      >
        Create a new Note
      </Typography>

      <form noValidate autoComplete="off" onSubmit={onSubmit}>
        <TextField
          onChange={e => setTitle(e.target.value)}
          className={classes.field}
          label='Note title'
          color='primary'
          variant='outlined'
          fullWidth
          error={titleError}
          required
        />

        <TextField
          onChange={e => setDetails(e.target.value)}
          className={classes.field}
          label='Note details'
          color='primary'
          variant='outlined'
          fullWidth
          multiline
          row={4}
          error={detailsError}
          required
        />

        <FormControl className={classes.field}>
          <FormLabel>Note Category</FormLabel>
          <RadioGroup value={category} onChange={e => setCategory(e.target.value)}>
            <FormControlLabel value='money' control={<Radio />} label='Money'></FormControlLabel>
            <FormControlLabel value='todos' control={<Radio />} label='Todos'></FormControlLabel>
            <FormControlLabel value='reminders' control={<Radio />} label='Reminders'></FormControlLabel>
            <FormControlLabel value='work' control={<Radio />} label='Work'></FormControlLabel>
          </RadioGroup>
        </FormControl>
        <Button
          className={classes.btn}
          onClick={() => console.log('You clicked me')}
          type='submit'
          color='primary'
          variant='contained'
          endIcon={<KeyboardArrowRight />}
        >
          Submit
      </Button>
      </form>
    </Container>
  )
}
