import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import clsx from 'clsx';
import { Typography, Button, Container, FormControlLabel, FormLabel, FormControl } from '@material-ui/core';
import { KeyboardArrowRight } from '@material-ui/icons';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import IconButton from '@material-ui/core/IconButton';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import PersonIcon from '@material-ui/icons/Person';
import EmailIcon from '@material-ui/icons/Email';
import LockIcon from '@material-ui/icons/Lock';

const useStyles = makeStyles({
  container: {
    textAlign: 'center',
    width: 700,
    marginTop: 30
  },
  title: {
    // margin: '20px 0'
  },
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  },
  form: {
    marginTop: 20,
    marginBottom: 20,
  },
  label: {
    display: 'flex',
    alignItems: 'center'
  },
  icon: {
    marginRight: 5
  },
  button: {
    marginTop: 20
  }
})



const Signup = () => {
  const classes = useStyles();

  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
    showPassword1: false,
    showPassword2: false,
  });

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [password1Error, setPassword1Error] = useState(false);
  const [password2Error, setPassword2Error] = useState(false);

  const history = useHistory();

  const handleChange = (prop) => (e) => {
    setValues({ ...values, [prop]: e.target.value });
  };

  const handleClickShowPassword1 = () => {
    setValues({ ...values, showPassword1: !values.showPassword1 });
  };

  const handleClickShowPassword2 = () => {
    setValues({ ...values, showPassword2: !values.showPassword2 });
  };

  const handleMouseDownPassword = (e) => {
    e.preventDefault();
  };

  const onSubmit = e => {
    e.preventDefault();
    setNameError(false);
    setEmailError(false);
    setPassword1Error(false);
    setPassword2Error(false);

    if (values.name == '') {
      setNameError(true)
    }

    if (values.email == '') {
      setEmailError(true)
    }

    if (values.password == '') {
      setPassword1Error(true)
    }

    if (values.password2 == '') {
      setPassword2Error(true)
    }

    if (values.name && values.email && values.password && values.password2) {
      console.log('Everything is filled')
    }
  }

  return (

    <Container className={classes.container}>
      <Typography
        variant='h5'
        component='p'
        color='textSecondary'
      >
        Join Diary
      </Typography>

      <Typography
        className={classes.title}
        variant='h4'
        component='h2'
        color='default'
        gutterBottom
      >
        Create your account
      </Typography>

      <form noValidate autoComplete="off" onSubmit={onSubmit}>
        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel className={classes.label} required><PersonIcon className={classes.icon} fontSize="small" />Name</InputLabel>
          <OutlinedInput
            value={values.name}
            error={nameError}
            onChange={handleChange('name')}
            labelWidth={78}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel className={classes.label} required><EmailIcon className={classes.icon} fontSize="small" />Email</InputLabel>
          <OutlinedInput
            value={values.email}
            error={emailError}
            onChange={handleChange('email')}
            labelWidth={75}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel className={classes.label} required><LockIcon className={classes.icon} fontSize="small" />Password</InputLabel>
          <OutlinedInput
            type={values.showPassword1 ? 'text' : 'password'}
            value={values.password}
            error={password1Error}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword1}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={105}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel className={classes.label} required><LockIcon className={classes.icon} fontSize="small" />Confirm password</InputLabel>
          <OutlinedInput
            type={values.showPassword2 ? 'text' : 'password'}
            value={values.password2}
            error={password2Error}
            onChange={handleChange('password2')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword2}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={170}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <Button
          className={classes.button}
          // disabled
          onClick={() => console.log('You clicked me')}
          type='submit'
          color='primary'
          variant='contained'
          endIcon={<KeyboardArrowRight />}
        >
          Create account
      </Button>
      </form>
    </Container>
  )
}

export default Signup
