import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import clsx from 'clsx';
import { Typography, Button, Container, FormControlLabel, FormLabel, FormControl } from '@material-ui/core';
import { KeyboardArrowRight } from '@material-ui/icons';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import IconButton from '@material-ui/core/IconButton';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';


const useStyles = makeStyles({
  title: {
    margin: '20px 0'
  },
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  },
  form: {
    marginTop: 20,
    marginBottom: 20,
  }
})



const Signup = () => {
  const classes = useStyles();

  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
    showPassword1: false,
    showPassword2: false,
  });

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [password1Error, setPassword1Error] = useState(false);
  const [password2Error, setPassword2Error] = useState(false);

  const history = useHistory();

  const handleChange = (prop) => (e) => {
    setValues({ ...values, [prop]: e.target.value });
  };

  const handleClickShowPassword1 = () => {
    setValues({ ...values, showPassword1: !values.showPassword1 });
  };

  const handleClickShowPassword2 = () => {
    setValues({ ...values, showPassword2: !values.showPassword2 });
  };

  const handleMouseDownPassword = (e) => {
    e.preventDefault();
  };

  const onSubmit = e => {
    e.preventDefault();
    setNameError(false);
    setEmailError(false);
    setPassword1Error(false);
    setPassword2Error(false);

    if (values.name == '') {
      setNameError(true)
    }

    if (values.email == '') {
      setEmailError(true)
    }

    if (values.password == '') {
      setPassword1Error(true)
    }

    if (values.password2 == '') {
      setPassword2Error(true)
    }

    if (values.name && values.email && values.password && values.password2) {
      console.log('Everything is filled')
    }
  }

  return (

    <Container>
      <Typography
        className={classes.title}
        variant='h4'
        component='h2'
        color='textSecondary'
        gutterBottom
      >
        Create a new account
      </Typography>

      <form noValidate autoComplete="off" onSubmit={onSubmit}>
        {/* <TextField
          value={values.name}
          onChange={handleChange('name')}
          className={classes.field}
          label='Name'
          color='primary'
          variant='outlined'
          fullWidth
          error={nameError}
          required
        />

        <TextField
          value={values.email}
          onChange={handleChange('email')}
          className={classes.field}
          label='Email'
          color='primary'
          variant='outlined'
          fullWidth
          multiline
          row={4}
          error={emailError}
          required
        /> */}

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel required>Name</InputLabel>
          <OutlinedInput
            value={values.name}
            onChange={handleChange('name')}
            labelWidth={60}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel required>Email</InputLabel>
          <OutlinedInput
            value={values.email}
            onChange={handleChange('email')}
            labelWidth={70}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel required>Password</InputLabel>
          <OutlinedInput
            type={values.showPassword1 ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword1}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={85}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <FormControl className={classes.form} fullWidth variant="outlined">
          <InputLabel required>Confirm password</InputLabel>
          <OutlinedInput
            type={values.showPassword2 ? 'text' : 'password'}
            value={values.password2}
            onChange={handleChange('password2')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword2}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={150}
          />
          {/* <FormHelperText>Password is required</FormHelperText> */}
        </FormControl>

        <Button
          onClick={() => console.log('You clicked me')}
          type='submit'
          color='primary'
          variant='contained'
          endIcon={<KeyboardArrowRight />}
        >
          Submit
      </Button>
      </form>
    </Container>
  )
}

export default Signup
