import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import clsx from 'clsx';
import { Typography, Button, Container, FormControlLabel, FormLabel, FormControl } from '@material-ui/core';
import { KeyboardArrowRight } from '@material-ui/icons';
import { makeStyles } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import IconButton from '@material-ui/core/IconButton';
import InputAdornment from '@material-ui/core/InputAdornment';

const useStyles = makeStyles({
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: 'block'
  }
})



const Signup = () => {
  const classes = useStyles();

  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
    showPassword: false
  });

  const [nameError, setNameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [password2Error, setPassword2Error] = useState(false);

  const history = useHistory();

  const handleChange = (prop) => (e) => {
    setValues({ ...values, [prop]: e.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (e) => {
    e.preventDefault();
  };

  const onSubmit = e => {
    e.preventDefault();
    setNameError(false);
    setEmailError(false);
    setPasswordError(false);
    setPassword2Error(false);

    if (values.name == '') {
      setNameError(true)
    }

    if (values.email == '') {
      setEmailError(true)
    }

    if (values.password == '') {
      setPasswordError(true)
    }

    if (values.password2 == '') {
      setPassword2Error(true)
    }

    if (values.name && values.email && values.password && values.password2) {
      console.log('Everything is filled')
    }
  }

  return (

    <Container>
      <Typography
        className={classes.title}
        variant='h6'
        component='h2'
        color='textSecondary'
        gutterBottom
      >
        Create a new account
      </Typography>

      <form noValidate autoComplete="off" onSubmit={onSubmit}>
        <TextField
          value={values.name}
          onChange={handleChange('name')}
          className={classes.field}
          label='Name'
          color='primary'
          variant='outlined'
          fullWidth
          error={nameError}
          required
        />

        <TextField
          value={values.email}
          onChange={handleChange('email')}
          className={classes.field}
          label='Email'
          color='primary'
          variant='outlined'
          fullWidth
          multiline
          row={4}
          error={emailError}
          required
        />

        {/* <TextField
          value={values.password}
          onChange={handleChange('password')}
          className={classes.field}
          label='Password'
          color='primary'
          variant='outlined'
          type={values.showPassword ? 'text' : 'password'}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                onClick={handleClickShowPassword}
                onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                {values.showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          }
          fullWidth
          multiline
          row={4}
          error={passwordError}
          required
        /> */}

        <FormControl className={clsx(classes.margin, classes.textField)} fullWidth variant="outlined">
          <InputLabel required>Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            labelWidth={100}
          />
        </FormControl>

        <TextField
          value={values.password2}
          onChange={handleChange('password2')}
          className={classes.field}
          label='Confirm password'
          type='password'
          color='primary'
          variant='outlined'
          fullWidth
          multiline
          row={4}
          error={password2Error}
          required
        />

        <Button
          onClick={() => console.log('You clicked me')}
          type='submit'
          color='primary'
          variant='contained'
          endIcon={<KeyboardArrowRight />}
        >
          Submit
      </Button>
      </form>
    </Container>
  )
}

export default Signup
